import React, { Component } from 'react';
import { render } from "react-dom";
// import GoogleMapReact from 'google-map-react';
// import GoogleMap from 'google-map-react';
// import $ from 'jquery';
import Maps from './Maps'
const GOOGLE_API_KEY = "AIzaSyCIJkPLyzo5XZODdhxAa_XYbk2ESQpJyZw";
class MapUpdate extends Component {
    constructor(props) {
      super(props);
      this.state = {
        center: {
          lat: 37.7824134,
          lng: -122.4088472
        },
        form: {
          lat: 37.7824134,
          lng: -122.4088472
        }
      };
      this.handleChange = this.handleChange.bind(this);
      this.handleClick = this.handleClick.bind(this);
    }

    handleChange() {
      this.setState({
        form: {
          lat: Number(this.refs.lat.value),
          lng: Number(this.refs.lng.value)
        }
      });
    }

    handleClick() {
      this.setState({
        center: this.state.form
      });
    }

    render() {
      const center = this.state.center;
      return (
        <div style={{ width: "100%", height: "100%" }}>
          <div>
            <input
              ref="lat"
              type="text"
              value={this.state.form.lat}
              onChange={this.handleChange}
            />
            <input
              ref="lng"
              type="text"
              value={this.state.form.lng}
              onChange={this.handleChange}
            />
            <input onClick={this.handleClick} type="button" value="update" />
          </div>
          <div style={{ width: "100%", height: "100%" }}>
            <Maps center={center} zoom={15} />
          </div>
        </div>
      );
    }
  }

  export default MapUpdate;


  if (GOOGLE_API_KEY === "AIzaSyC4Z5Qz97EWcoCczNn2IcYvaYG0L9pe6Rk") {
    render(
      <div>
        <h1>Enter Google API Key in GOOGLE_API_KEY const</h1>
      </div>,
      document.getElementById("root")
    );
  } else {
    render(
      <div style={{ width: "100%", height: "500px" }}>
        <MapUpdate />
      </div>,
      document.getElementById("root")
    );
  }
