import React, { Component } from 'react';
import Maps from './Maps';
import $ from 'jquery';

// import SearchFilter from '../SearchFilter';
// import currentCenters from "../../pages/OurCenters/OurCenters";

const GOOGLE_API_KEY = "AIzaSyCIJkPLyzo5XZODdhxAa_XYbk2ESQpJyZw";

const centersLocation = JSON.parse(localStorage.getItem("centres"));

const dd = Object.entries(centersLocation.data).map((item,k) => {
  // console.log(item[1].lng);
    return <>
      lng={item[1].lng}
      lat={item[1].lat}
      </>

})

class MapUpdate extends Component {
    constructor(props) {
      super(props);

      this.state = {
        center: {
          lat: 37.7824134,
          lng: -122.4088472
        },
        form: {
          lat: 37.7824134,
          lng: -122.4088472
        }
      };
      // this.handleChange = this.handleChange.bind(this);
      this.handleClick = this.handleClick.bind(this);
    }

    // handleChange() {
    //   this.setState({
    //     form: {
    //       lat: Number(this.refs.lat.value),
    //       lng: Number(this.refs.lng.value)
    //     }
    //   });
    // }




    handleClick(event) {
      let cityId = parseInt($("#search_filter").val());
      var latt = 0;
      var long = 0;
      $.each(centersLocation.data, function(k, v) {
        if(v.city == cityId){latt = v.lat;long = v.lng;console.log(v.city);return;}
      });
      console.log(centersLocation.data);
      this.setState({
        center: {lat:latt,lng:long}
      });
    }

    render() {
      const center = this.state.center;

      return (
        <div style={{ width: "100%", height: "100%" }}>
          <div>
            <input onClick={this.handleClick} type="button" id="update_map" data-tag="Update" />
          </div>
          <div style={{ width: "100%", height: "100%" }}>
            <Maps center={center} zoom={5} />
          </div>
        </div>
      );
    }
  }

  export default MapUpdate;
