import React, {useState, useEffect} from 'react';
import Axios from 'axios';
import {withRouter, NavLink} from "react-router-dom";
import logo from '../../assets/kido-logo.jpg'

function Header(props){

  const [selectedOption, setSelectedOption] = useState("everyone");

  useEffect(() => {
    // getCentersData();
    getCitiesData();
}, []);

  const getCitiesData = () => {
    Axios
    .get(`https://kido-school-new-default-rtdb.firebaseio.com/cities.json`)
    .then((response) => {
      // console.log(response);
      setCitiesContents(response.data);
    })
    .catch((error) => console.log(error));
  };

  const handleOnChange = (e) => {
    setSelectedOption(e.target.value)
    setCurrentPage(1)
  };


    return(
      <>
       <header>
        <nav className="navbar navbar-expand-lg navbar-light bg-light bg-color fixed-top">
          <NavLink className="navbar-brand pl-4" to="/"><img src={logo} alt="logo"/></NavLink>
          <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>


          <form id="search" className=" navbar-form search-form" role="search">
            <div className="input-group"> 
              <input type="text" className="form-control" placeholder="Centre location or name" name="srch-term" id="srch-term"/>
                <div className="input-group-btn"> 
                  <select className="selectpicker">
                    {/* <option id="myLocationOption" value="myLocation" className="d-none">My location</option> */}
                    <option id="allCitiesOption" value="-" defaultValue="selected">Everywhere</option>
                    <option value="Austin">Austin</option>
                    <option value="Houston">Houston</option>
                    <option value="Greater London">Greater London</option>
                    <option value="Dubai">Dubai</option>
                    <option value="India">India</option>
                    <option value="Hong Kong">Hong Kong</option> 
                  </select>
                </div>
            </div>
          </form>

          <form id="search" className=" navbar-form search-form" role="search">
            <div className="input-group"> 
              <input type="text" className="form-control" placeholder="Centre location or name" name="srch-term" id="srch-term"/>
                <div className="input-group-btn"> 
                  <select className="selectpicker"  
                    value={selectedOption}
                    onChange={handleOnChange}>
                    {/* <option id="myLocationOption" value="myLocation" className="d-none">My location</option> */}
                    <option id="allCitiesOption" value="-" defaultValue="selected">Everywhere</option>
                    { Object.entries(citiesContents).map(item => (
                        <option value={item[1].id}>{item[1].name}</option>
                      ))}
                  </select>
                </div>
            </div>
          </form>


          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav mr-auto w-100 justify-content-end clearfix">
              <li className="nav-item">
                <NavLink className="nav-link pl-4" activeclassname="selected" to="/ourcentres">Find a nursery</NavLink>
              </li>
              <li className="nav-item">
                <NavLink className="nav-link pl-4" to="/about-us">About Us</NavLink>
              </li>
              <li className="nav-item">
                <NavLink className="nav-link pl-4" to="/programs">Our Programs</NavLink>
              </li>
              <li className="nav-item">
                <NavLink className="nav-link pl-4" to="/kido-home">Kido Home</NavLink>
              </li>
              <li className="nav-item">
                <NavLink className="nav-link pl-4" to="/contact-us">Contact</NavLink>
              </li>
              <li className="nav-item">
                <NavLink className="nav-link pl-4" to="/carrers">Careers</NavLink>
              </li>
              <li className="nav-item">
                <NavLink className="nav-link pl-4" to="/news-events">Blog</NavLink>
              </li>
            </ul>
          </div>
        </nav>
      </header>
      </>

    );

}

export default withRouter(Header);
