import React, {useState, useEffect} from 'react';
import Axios from 'axios';
import Pagination from "react-js-pagination";
import $ from 'jquery';

function OurCenters(props) {

  const [centerContents, setCenterContents] = useState({});
  const [citiesContents, setCitiesContents] = useState({});
  const [selectedOption, setSelectedOption] = useState("everyone");
  const todosPerPage = 5;
  const [ activePage, setCurrentPage ] = useState( 1 );

  useEffect(() => {
    getCentersData();
    getCitiesData();
}, []);

  const getCentersData = () => {
    Axios
    .get(`https://kido-school-new-default-rtdb.firebaseio.com/centers.json`)
    .then((response) => {
      // console.log(response);
      setCenterContents(response.data);
    })
    .catch((error) => console.log(error));
};

const getCitiesData = () => {
  Axios
  .get(`https://kido-school-new-default-rtdb.firebaseio.com/cities.json`)
  .then((response) => {
    // console.log(response);
    setCitiesContents(response.data);
  })
  .catch((error) => console.log(error));
};


// Logic for displaying current todos
const indexOfLastTodo  = activePage * todosPerPage;
const indexOfFirstTodo = indexOfLastTodo - todosPerPage;

var filteredOptions = [];

var centresList = Object.entries(centerContents);

if(selectedOption){
  $.each(centresList, function (k, v) {
    if(v[1].city == parseInt(selectedOption)){
      filteredOptions.push(v);
    }
  });
}

if(filteredOptions.length){
  centresList = filteredOptions;
}

 var currentCenters   = centresList.slice( indexOfFirstTodo, indexOfLastTodo );

// console.log(currentCenters,"--------------------CC");
// console.log(filteredOptions,"--------------------FO");

const handlePageChange = ( pageNumber ) => {
  console.log( `active page is ${ pageNumber }` );
  setCurrentPage( pageNumber )
};

const handleOnChange = (e) => {
  setSelectedOption(e.target.value)
  setCurrentPage(1)
};

 function printCentres(centreList) {

   const renderTodos = centreList.map((item,k) => {
    return (
    <div className="find-our-centers" key={item[0]}>
      <div className="card mb-3">
        <div className="row no-gutters">
          <div className="col-md-4">
            <div style={{backgroundImage: `url(${item[1].img})`}} className="card-img"></div>
            <div className="label">India</div>
          </div>
          <div className="col-md-8">
            <div className="card-body">
              <p className="card-title name"><a href="">{item[1].name}</a></p>
              <div className="row justify-content-center">
                <div className="col-lg-4 col-4 text-center">
                  <div className="card-icon1 py-2"><img src={'/'+(item[1].features[0].feature.slug) +".svg"} className="img-fluid" width="30"/></div>
                  <p className="card-text sch-config-text">{item[1].features[0].feature.description}</p>
                  <p className="card-text"><small className="text-muted">{item[1].features[0]["feature_information"]}</small></p>
                </div>
                <div className="col-lg-4 col-4 text-center">
                  <div className="card-icon2 py-2"><img src={'/'+(item[1].features[1].feature.slug) +".svg"} className="img-fluid" width="30"/></div>
                  <p className="card-text sch-config-text">{item[1].features[1].feature.description}</p>
                  <p className="card-text"><small className="text-muted">{item[1].features[1]["feature_information"]}</small></p>
                </div>
                <div className="col-lg-4 col-4 text-center">
                  <div className="card-icon2 py-2"><img src={'/'+(item[1].features[2].feature.slug) +".svg"} className="img-fluid" width="30"/></div>
                  <p className="card-text sch-config-text">{item[1].features[2].feature.description}</p>
                  <p className="card-text"><small className="text-muted">{item[1].features[2]["feature_information"]}</small></p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>

      )
      })
      return renderTodos;
 }

  return(
    <>

      <section className="main-banner pt-5">
      <div className="container">
        <div className="row filter-search">
         <form id="search" className=" navbar-form search-form" role="search">
            <div className="input-group"> 
              <input type="text" className="form-control" placeholder="Centre location or name" name="srch-term" id="srch-term"/>
                <div className="input-group-btn"> 
                  <select className="selectpicker"  
                    value={selectedOption}
                    onChange={handleOnChange}>
                    <option id="allCitiesOption" value="-" defaultValue="selected">Everywhere</option>
                    { Object.entries(citiesContents).map(item => (
                        <option value={item[1].id}>{item[1].name}</option>
                      ))}
                  </select>
                </div>
            </div>
          </form>
          </div>
          </div>
          </section>


      <section className="main-banner pt-5">
      <div className="container">
        <div className="row">
          <div className="col-lg-8">
          <p className="text no-margin pt-4"> Explore Our centres across the globe</p>
          <p id="number" className="text no-margin">We found 32 centres</p>
          </div>
        </div>
        <div className="row justify-content-center py-4">
          <div className="col-lg-7">
          { printCentres(currentCenters)  }

          <div className="pagination pt-3">
            <Pagination
               activePage={ activePage }
               itemsCountPerPage={ 5 }
               totalItemsCount={ centresList.length }
               pageRangeDisplayed={ 5 }
               onChange={ handlePageChange }
            />
         </div>
          </div>

           <div className="col-lg-5">
              <div className="map">
                <iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d30676237.522496227!2d66.00704712498575!3d20.189964549751675!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3be7c93e5cd549dd%3A0x7e2c99ce1f77bb31!2sSafari%20Kid%20Preschool%20%26%20Nursery%20Bandra!5e0!3m2!1sen!2sin!4v1609834162302!5m2!1sen!2sin" frameBorder="0" style={{border:"0"}} allowFullScreen="" aria-hidden="false" tabIndex="0"></iframe>
              </div>
           </div>
       </div>
      </div>
    </section>

    </>
  );

}

export default OurCenters;
