import React, {useState, useEffect} from 'react';
import Axios from 'axios';
import center1 from '../../assets/centers-img1.jpg';
import Pagination from "react-js-pagination";

function OurCenters(props) {

  const [centerContents, setCenterContents] = useState({});
  const todosPerPage = 5;
  const [ activePage, setCurrentPage ] = useState( 1 );

  useEffect(() => {
    getCentersData();
}, []);

  const getCentersData = () => {
    Axios
    .get(`https://kido-school-new-default-rtdb.firebaseio.com/centers.json`)
    .then((response) => {
      console.log(response);
      setCenterContents(response.data);
    })
    .catch((error) => console.log(error));
};

 // Logic for displaying current todos
 const indexOfLastTodo  = activePage * todosPerPage;
 const indexOfFirstTodo = indexOfLastTodo - todosPerPage;
 const currentTodos     = Object.entries(centerContents).slice( indexOfFirstTodo, indexOfLastTodo );

 const renderTodos = currentTodos.map((item,k) => {
  return (
  <div className="find-our-centers">
    <div className="card mb-3">
      <div className="row no-gutters">
        <div className="col-md-4">
          <div style={{backgroundImage: `url(${item[1].img})`}} className="card-img"></div>
          <div className="label">India</div>
        </div>
        <div className="col-md-8">
          <div className="card-body">
            <p className="card-title name"><a href="">{item[1].name}</a></p>
            <div className="row justify-content-center">
              <div className="col-lg-4 col-4 text-center">
                <div className="card-icon py-2"><i className="fas fa-map-marked-alt"></i></div>
                <p className="card-text sch-config-text">Center Area</p>
                <p className="card-text"><small className="text-muted">15,000 sq ft</small></p>
              </div>
              <div className="col-lg-4 col-4 text-center">
                <div className="card-icon py-2"><i className="fas fa-language"></i></div>
                <p className="card-text sch-config-text">Languages Taught</p>
                <p className="card-text"><small className="text-muted">Spanish and English</small></p>
              </div>
              <div className="col-lg-4 col-4 text-center">
                <div className="card-icon py-2"><i className="fas fa-user-friends"></i></div>
                <p className="card-text sch-config-text">Age 	&nbsp;&nbsp;Group</p>
                <p className="card-text"><small className="text-muted">6 weeks to 5 years</small></p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>

    )
    })

    const handlePageChange = ( pageNumber ) => {
      console.log( `active page is ${ pageNumber }` );
      setCurrentPage( pageNumber )
   };


  return(
    <>
    <section className="main-banner pt-5">
      <div className="container">
        <div className="row">
          <div className="col-lg-8">
          <p className="text no-margin pt-4"> Explore Our centres across the globe</p>
          <p id="number" className="text no-margin">We found 32 centres</p>
          </div>
        </div>
        <div className="row justify-content-center py-4">
          <div className="col-lg-7">
          { renderTodos }

          <nav aria-label="Page navigation example">
              <ul class="pagination">
                <li class="page-item p-3">
                  <Pagination
                    activePage={ activePage }
                    itemsCountPerPage={ 5 }
                    totalItemsCount={ centerContents.length }
                    pageRangeDisplayed={ 5 }
                    onChange={ handlePageChange }
                  />
                </li>
              </ul>
          </nav>
          </div>

           <div className="col-lg-5">
              <div className="map">
                <iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d30676237.522496227!2d66.00704712498575!3d20.189964549751675!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3be7c93e5cd549dd%3A0x7e2c99ce1f77bb31!2sSafari%20Kid%20Preschool%20%26%20Nursery%20Bandra!5e0!3m2!1sen!2sin!4v1609834162302!5m2!1sen!2sin" frameBorder="0" style={{border:"0"}} allowFullScreen="" aria-hidden="false" tabIndex="0"></iframe>
              </div>
           </div>
       </div>
      </div>
    </section>

    </>
  );

}

export default OurCenters;
