import React, { Component } from 'react';
import GoogleMapReact from 'google-map-react';
// import $ from 'jquery';

const AnyReactComponent = ({ text }) => <div>{<i className="text-primary fas fa-map-marker-alt fa-2x"></i>}</div>;

class Maps extends Component {
  static defaultProps = {
    center: {
      lng: 72.89,
      lat: 19.04
    },
    zoom: 10
  };

  // componentDidMount(){

  //   $("#search_filter").on("change", function() {
  //     console.log("ashghgd");
  //    });

  // }

  render() {

    let centersLocation = JSON.parse(localStorage.getItem("centres"));
    // console.log(centersLocation);

    return (
      // Important! Always set the container height explicitly
      <div id="gmap" style={{ height: '520px', width: '100%' }}>
        <GoogleMapReact
          bootstrapURLKeys={{ key:"AIzaSyCIJkPLyzo5XZODdhxAa_XYbk2ESQpJyZw"}}
          defaultCenter={this.props.center}
          defaultZoom={this.props.zoom}
        >

          {Object.entries(centersLocation.data).map((item,k) => {
            // console.log(item[1].lng);
              return <AnyReactComponent
                lng={item[1].lng}
                lat={item[1].lat}
                text="My Marker"
              />
          })
        }

        </GoogleMapReact>
      </div>
    );
  }
}

export default Maps;

class MapUpdate extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      center: {
        lat: 37.7824134,
        lng: -122.4088472
      },
      form: {
        lat: 37.7824134,
        lng: -122.4088472
      }
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  handleChange() {
    this.setState({
      form: {
        lat: Number(this.refs.lat.value),
        lng: Number(this.refs.lng.value)
      }
    });
  }

  handleClick() {
    this.setState({
      center: this.state.form
    });
  }

  render() {
    const center = this.state.center;
    return (
      <div style={{ width: "100%", height: "100%" }}>
        <div>
          <input
            ref="lat"
            type="text"
            value={this.state.form.lat}
            onChange={this.handleChange}
          />
          <input
            ref="lng"
            type="text"
            value={this.state.form.lng}
            onChange={this.handleChange}
          />
          <input onClick={this.handleClick} type="button" value="update" />
        </div>
        <div style={{ width: "100%", height: "100%" }}>
          <GMapReact center={center} zoom={15} />
        </div>
      </div>
    );
  }
}

if (GOOGLE_API_KEY === "ENTER_API_KEY_HERE") {
  render(
    <div>
      <h1>Enter Google API Key in GOOGLE_API_KEY const</h1>
    </div>,
    document.getElementById("root")
  );
} else {
  render(
    <div style={{ width: "100%", height: "500px" }}>
      <MapUpdate />
    </div>,
    document.getElementById("root")
  );
}
